mapper.select:

 		select rn, freeboardNum, freeboardTitle, freeboardwriter, freeboarddate, freeboardreadcount from(
   	     	select rownum as rn, freeboardNum, freeboardTitle, freeboardwriter, freeboarddate, freeboardreadcount 
        	from freeboard where rownum <= #{pageNum} * #{amount} order by freeboardNum desc) 
		where rn > (#{pageNum}-1) * #{amount}
